    /**
     * Ê†ºÂºèÂåñÊó•Êúü
     */
    formatDate(date) {
      if (typeof date === 'string') {
        date = new Date(date);
      }
      return date.toLocaleString('zh-CN', {
        year: 'numeric',
        month: '2-digit',
