        callback(new Error('时长必须大于0'));
      } else {
        callback();
      }
    };
    return {
      // 工作点数据
      workpoints: [],
      // 动态加载工作点列表
      activeWorkpoint: '',
      // 当前激活的工作点（动态设置为第一个）

      tableData: [],
      pageSize: 20,
      pageNum: 1,
      total: 0,
      processName: '',
      stage: '',
      isShared: '',
      isParallel: '',
      saveLoading: false,
      multipleSelection: [],
      stages: [{
        value: '1',
        label: '第1阶段'
      }, {
        value: '2',
