      }, {
        value: '9',
        label: '第9阶段'
      }, {
        value: '10',
        label: '第10阶段'
      }],
      sharedTypes: [{
        value: 'false',
        label: '专用'
      }, {
        value: 'true',
        label: '共用'
      }],
      parallelTypes: [{
        value: 'false',
        label: '非并行'
      }, {
        value: 'true',
        label: '可并行'
      }],
      centerDialogVisible: false,
      form: {
        id: '',
        processName: '',
        processOrder: 1,
        teamName: 'team1',
        isDedicated: false,
        teamSize: 10,
        duration: 10,
        isParallel: false,
        workpointId: ''
      },
      rules: {
        processName: [{
          required: true,
          message: '请输入工序名称',
          trigger: 'blur'
        }, {
          min: 1,
          max: 50,
          message: '长度在 1 到 50 个字符',
          trigger: 'blur'
        }],
        duration: [{
          required: true,
          message: '请输入时长',
          trigger: 'blur'
        }, {
          validator: checkDuration,
          trigger: 'blur'
        }],
        processOrder: [{
          required: true,
          message: '请输入阶段顺序',
          trigger: 'blur'
        }],
        teamName: [{
          required: true,
          message: '请选择团队',
          trigger: 'blur'
        }],
        teamSize: [{
          required: true,
          message: '请输入团队规模',
          trigger: 'blur'
        }]
      },
      // Excel导入相关
      importDialogVisible: false,
      fileList: [],
      importing: false,
      importProgress: 0,
      importStatus: '',
      importMessage: '',
      importResult: null
    };
  },
  methods: {
    handleWorkpointChange() {
      // 切换工作点时重置分页并重新加载数据
      this.pageNum = 1;
      this.resetParam();
    },
    handleSelectionChange(val) {
      this.multipleSelection = val;
    },
    getStageType(stage) {
      const types = ['', 'primary', 'success', 'info', 'warning', 'danger'];
      return types[stage] || 'info';
    },
    formatTime(time) {
      if (!time) return '--';
      const date = new Date(time);
      return date.toLocaleString('zh-CN', {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit'
      });
    },
    resetForm() {
      this.$refs.form.resetFields();
    },
    add() {
      this.centerDialogVisible = true;
      this.$nextTick(() => {
        this.resetForm();
        this.form = {
          id: '',
          processName: '',
          processOrder: 1,
          teamName: 'team1',
          isDedicated: false,
          teamSize: 10,
          duration: 10,
          isParallel: false,
          workpointId: this.activeWorkpoint
        };
      });
    },
    del(id) {
      this.$axios.get(this.$httpUrl + `/process/${this.activeWorkpoint}/del?id=` + id).then(res => res.data).then(res => {
        if (res.code == 200) {
          this.$message({
            message: '删除成功!',
            type: 'success'
          });
          this.loadPost();
        } else {
          this.$message({
            message: '删除失败!',
            type: 'error'
          });
        }
      });
    },
    mod(row) {
      this.centerDialogVisible = true;
      this.$nextTick(() => {
        this.form.id = row.id;
        this.form.processName = row.processName;
        this.form.processOrder = row.processOrder;
        this.form.teamName = row.teamName;
        this.form.isDedicated = row.isDedicated;
        this.form.teamSize = row.teamSize;
        this.form.duration = row.duration;
        this.form.isParallel = row.isParallel;
        this.form.workpointId = this.activeWorkpoint;
      });
    },
    doSave() {
      this.saveLoading = true;
      this.form.workpointId = this.activeWorkpoint;
      this.$axios.post(this.$httpUrl + `/process/${this.activeWorkpoint}/save`, this.form).then(res => res.data).then(res => {
        this.saveLoading = false;
        if (res.code == 200) {
          this.$message({
            message: '保存成功!',
            type: 'success'
          });
          this.centerDialogVisible = false;
          this.loadPost();
        } else {
          this.$message({
            message: '保存失败!',
            type: 'error'
          });
        }
      });
    },
    doMod() {
      this.saveLoading = true;
      this.form.workpointId = this.activeWorkpoint;
      this.$axios.post(this.$httpUrl + `/process/${this.activeWorkpoint}/update`, this.form).then(res => res.data).then(res => {
        this.saveLoading = false;
        if (res.code == 200) {
          this.$message({
            message: '修改成功!',
            type: 'success'
          });
          this.centerDialogVisible = false;
          this.loadPost();
        } else {
          this.$message({
            message: '修改失败!',
            type: 'error'
          });
        }
      });
    },
    save() {
      this.$refs.form.validate(valid => {
        if (valid) {
          if (this.form.id) {
            this.doMod();
          } else {
            this.doSave();
          }
        } else {
          this.$message.warning('请检查表单填写是否正确');
          return false;
        }
      });
    },
    resetParam() {
      this.processName = '';
      this.stage = '';
      this.isShared = '';
      this.isParallel = '';
      this.loadPost();
    },
    loadPost() {
      // 使用新的动态API
      this.$axios.post(this.$httpUrl + `/process/${this.activeWorkpoint}/listPageC1`, {
        pageNum: this.pageNum,
        pageSize: this.pageSize,
        param: {
          processName: this.processName,
          processOrder: this.stage,
          isDedicated: this.isShared === 'false' ? true : this.isShared === 'true' ? false : '',
          isParallel: this.isParallel
        }
      }).then(res => res.data).then(res => {
        if (res.code == 200) {
          this.tableData = res.data;
          this.total = res.total;

          // 更新工作点的工序数量
          const wp = this.workpoints.find(w => w.id === this.activeWorkpoint);
          if (wp) {
            wp.count = res.total;
          }
        } else {
          this.$message.error('获取数据失败');
          this.tableData = [];
          this.total = 0;
        }
      }).catch(error => {
        console.error('加载数据失败:', error);
        this.$message.error('加载数据失败，请检查后端服务');
        this.tableData = [];
        this.total = 0;
      });
    },
    // 加载工作点列表（动态从数据库获取）
    loadWorkpoints() {
      this.$axios.get(this.$httpUrl + '/process/workpoints').then(res => res.data).then(res => {
        console.log('工作点列表响应:', res);
        if (res.code == 200) {
          // 后端返回格式：{ code: 200, data: [...], total: 5 }
          // 需要判断 data 是数组还是在 data 字段中
          let workpointList = Array.isArray(res.data) ? res.data : [];
          console.log('解析的工作点列表:', workpointList);
          if (workpointList.length > 0) {
            // 为每个工作点初始化count
            this.workpoints = workpointList.map(wp => ({
              id: wp.id,
              name: wp.name,
              count: 0
            }));
            console.log('设置的workpoints:', this.workpoints);

            // 设置第一个工作点为激活状态
            if (!this.activeWorkpoint && this.workpoints.length > 0) {
              this.activeWorkpoint = this.workpoints[0].id;
              console.log('激活的工作点:', this.activeWorkpoint);

              // 只有在 activeWorkpoint 被设置后才加载数据
              this.$nextTick(() => {
                // 加载第一个工作点的数据
                this.loadPost();

                // 加载所有工作点的数量统计
                this.loadAllWorkpointCounts();
              });
            }
          } else {
            this.$message.warning('未找到任何工作点表，请先在数据库中创建 process_workpoint_* 表');
            this.workpoints = [];
          }
        } else {
          this.$message.error('获取工作点列表失败: ' + (res.msg || '未知错误'));
          this.workpoints = [];
        }
      }).catch(error => {
        console.error('加载工作点列表失败:', error);
        this.$message.error('加载工作点列表失败，请检查后端服务');
        this.workpoints = [];
      });
    },
    // 加载所有工作点的工序数量
    loadAllWorkpointCounts() {
      this.workpoints.forEach(wp => {
        this.$axios.post(this.$httpUrl + `/process/${wp.id}/listPageC1`, {
          pageNum: 1,
          pageSize: 1,
          param: {}
        }).then(res => res.data).then(res => {
          if (res.code == 200) {
            wp.count = res.total;
          }
        }).catch(() => {
          wp.count = 0;
        });
      });
    },
    handleSizeChange(val) {
      this.pageNum = 1;
      this.pageSize = val;
      this.loadPost();
    },
    handleCurrentChange(val) {
      this.pageNum = val;
      this.loadPost();
    }
  },
  beforeMount() {
    // 首先加载工作点列表（动态获取）
    // loadWorkpoints 内部会自动调用 loadPost 和 loadAllWorkpointCounts
    this.loadWorkpoints();
  }
};
