    // 加载开卡数据
    loadPipelineCards() {
      this.$axios.post(this.$httpUrl + '/pipelineCard/listPageFlat', {
        pageNum: this.pipelinePageNum,
        pageSize: this.pipelinePageSize,
        param: {
          pipelineCode: this.pipelineSearchCode,
          status: this.pipelineStatus
