      }).then(res => res.data).then(res => {
        if (res.code === 200) {
          // 过滤掉表头行（管道编号为"管道编号"的记录）
          let data = res.data.data || [];
          this.pipelineCardData = data.filter(item => item.pipelineCode !== '管道编号' && item.cardNo !== '管道序号');
          // 调整总数
          const filteredCount = data.length - this.pipelineCardData.length;
          this.pipelineCardTotal = res.data.total - filteredCount;

          // 强制刷新表格布局
          this.$nextTick(() => {
            // Element UI 表格布局刷新
            if (this.$refs && this.$refs.pipelineTable) {
              this.$refs.pipelineTable.doLayout();
            }
          });
        } else {
          this.$message.error('加载开卡数据失败：' + res.msg);
        }
      }).catch(error => {
        console.error('加载开卡数据失败:', error);
        this.$message.error('加载开卡数据失败');
      });
    },
    // 重置搜索
    resetPipelineSearch() {
      this.pipelineSearchCode = '';
      this.pipelineStatus = '';
      this.pipelinePageNum = 1;
      this.loadPipelineCards();
    },
    // 分页大小改变
    handlePipelineSizeChange(val) {
      this.pipelinePageNum = 1;
      this.pipelinePageSize = val;
      this.loadPipelineCards();
    },
    // 当前页改变
    handlePipelineCurrentChange(val) {
      this.pipelinePageNum = val;
      this.loadPipelineCards();
    },
    // 选择变化
    handlePipelineSelectionChange(selection) {
      this.pipelineSelection = selection;
    },
    // 删除单条记录
    deletePipelineCard(id) {
      this.$axios.delete(this.$httpUrl + '/pipelineCard/delete/' + id).then(res => res.data).then(res => {
        if (res.code === 200) {
          this.$message.success('删除成功');
          this.loadPipelineCards();
        } else {
          this.$message.error('删除失败：' + res.msg);
        }
      }).catch(error => {
        console.error('删除失败:', error);
        this.$message.error('删除失败');
      });
    },
    // 批量删除
    batchDeletePipelines() {
      if (this.pipelineSelection.length === 0) {
        this.$message.warning('请选择要删除的记录');
        return;
      }
      this.$confirm('确定要删除选中的 ' + this.pipelineSelection.length + ' 条记录吗？', '批量删除', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        const ids = this.pipelineSelection.map(item => item.id);
        this.$axios.post(this.$httpUrl + '/pipelineCard/deleteBatch', ids).then(res => res.data).then(res => {
          if (res.code === 200) {
            this.$message.success('批量删除成功');
            this.loadPipelineCards();
          } else {
            this.$message.error('批量删除失败：' + res.msg);
          }
        }).catch(error => {
          console.error('批量删除失败:', error);
          this.$message.error('批量删除失败');
        });
      }).catch(() => {
        // 取消删除
      });
    },
    // 格式化单元格值
    formatCellValue(value) {
      if (value === null || value === undefined || value === '') {
        return '-';
      }
      if (value === ' ') {
        return '-';
      }
      return value;
    },
    // 获取单元格样式类
    getCellClass(value) {
      if (value === null || value === undefined || value === '' || value === ' ') {
        return 'cell-empty';
      }
      return 'cell-filled';
    }
  },
  beforeMount() {
    // 首先加载工作点列表（动态获取）
    // loadWorkpoints 内部会自动调用 loadPost 和 loadAllWorkpointCounts
    this.loadWorkpoints();
  }
};
