import { IWorkUnitCache } from "./workunit_cache_interface";

export class WorkUnitsMapCache implements IWorkUnitCache {
	private _cache: Map<string, Array<Map<string, boolean>>>;
	constructor() {
		this.clear();
	}

	getItem(unit: string, timestamp: string, value: Date): number|boolean {
		if (this._cache.has(unit)) {
			const unitCache = this._cache.get(unit);

			const subCache = unitCache[value.getFullYear()];
			if (subCache && subCache.has(timestamp)) {
				return subCache.get(timestamp);
			}
		}

		return -1;
	}
	setItem(unit: string, timestamp: string, value: boolean, rawValue: Date): void {
		if (!unit || !timestamp) {
			return;
		}

		const cache = this._cache;
		const year = rawValue.getFullYear();

		let unitCache;
		if (!cache.has(unit)) {
			unitCache = [];
			cache.set(unit, unitCache);
		} else {
			unitCache = cache.get(unit);
		}

		let yearCache = unitCache[year];
		if(!yearCache){
			yearCache = unitCache[year] = new Map<string, boolean>();
		}


		yearCache.set(timestamp, value);
	}
	clear(): void{
		this._cache = new Map<string, Array<Map<string, boolean>>>();
	}
}

import { IWorkUnitCache } from "./workunit_cache_interface";

export class WorkUnitsObjectCache implements IWorkUnitCache {
	private _cache: any;
	constructor() {
		this.clear();
	}

	getItem(unit: string, timestamp: string, value: Date): number|boolean {
		const cache = this._cache;
		if (cache && cache[unit]) {
			const units = cache[unit];
			if(units === undefined){
				return -1;
			}
			const subCache = units[value.getFullYear()];
			if (subCache && subCache[timestamp] !== undefined) {
				return subCache[timestamp];
			}
		}

		return -1;
	}
	setItem(unit: string, timestamp: string, value: boolean, rawValue: Date): void {
		if (!unit || !timestamp) {
			return;
		}

		const cache = this._cache;

		if (!cache) {
			return;
		}
		if (!cache[unit]) {
			cache[unit] = [];
		}
		const unitCache = cache[unit];

		const year = rawValue.getFullYear();
		let yearCache = unitCache[year];
		if(!yearCache){
			yearCache = unitCache[year] = {};
		}
		yearCache[timestamp] = value;
	}
	clear(): void{
		this._cache = {};
	}
}

export class LargerUnitsCache {
	private _weekCache: Map<number, number>;
	private _monthCache: Map<number, number>;
	private _calendar: any;
	constructor(calendar) {
		this.clear();
		this._calendar = calendar;
	}

	getMinutesPerWeek = (weekStart: Date) => {
		const key = weekStart.valueOf();

		if(this._weekCache.has(key)){
			return this._weekCache.get(key);
		}

		const calendar = this._calendar;
		const gantt = this._calendar.$gantt;

		let minutesPerWeek = 0;
		let start = gantt.date.week_start(new Date(weekStart));
		for(let i = 0; i < 7; i++){
			minutesPerWeek += calendar.getHoursPerDay(start) * 60;
			start = gantt.date.add(start, 1, "day");
		}

		this._weekCache.set(key, minutesPerWeek);
		return minutesPerWeek;
	};

	getMinutesPerMonth = (monthStart: Date) => {
		const key = monthStart.valueOf();

		if(this._monthCache.has(key)){
			return this._monthCache.get(key);
		}

		const calendar = this._calendar;
		const gantt = this._calendar.$gantt;

		let minutesPerMonth = 0;
		let start = gantt.date.week_start(new Date(monthStart));
		const nextMonth = gantt.date.add(start, 1, "month").valueOf();
		while(start.valueOf() < nextMonth){
			minutesPerMonth += calendar.getHoursPerDay(start) * 60;
			start = gantt.date.add(start, 1, "day");
		}

		this._monthCache.set(key, minutesPerMonth);
		return minutesPerMonth;
	};

	clear = (): void => {
		this._weekCache = new Map<number, number>();
		this._monthCache = new Map<number, number>();
	};
}

export class DateDurationCache  {
	private _cache: any;
	constructor() {
		this.clear();
	}

	_getCacheObject(startDate: Date, unit: string, step:number){
		const cache = this._cache;
		if (!cache[unit]) {
			cache[unit] = [];
		}
		let unitCache = cache[unit];
		if(!unitCache) {
			unitCache = cache[unit] = {};
		}

		let stepCache = unitCache[step];
		if(!stepCache) {
			stepCache = unitCache[step] = {};
		}

		const year = startDate.getFullYear();
		let yearCache = stepCache[year];
		if(!yearCache){
			yearCache = stepCache[year] = {durations: {}, endDates: {}};
		}

		return yearCache;
	}
	_endDateCacheKey(startDate: number, duration: number){
		return String(startDate) + "-" + String(duration);
	}
	_durationCacheKey(startDate: number, endDate: number){
		return String(startDate) + "-" + String(endDate);
	}
	getEndDate(startDate: Date, duration: number, unit: string, step: number, compute: () => Date): number|null{
		const cache = this._getCacheObject(startDate, unit, step);

		const startDateTimestamp = startDate.valueOf();
		const key = this._endDateCacheKey(startDateTimestamp, duration);
		let endDate;
		if(cache.endDates[key] === undefined){
			const result = compute();
			const resultTimestamp = result.valueOf();
			cache.endDates[key] = resultTimestamp;
			cache.durations[this._durationCacheKey(startDateTimestamp, resultTimestamp)] = duration;
			endDate = result;
		}else{
			endDate = new Date(cache.endDates[key]);
		}

		return endDate;
	}

	getDuration(startDate: Date, endDate: Date, unit: string, step: number, compute: () => number): number|null{
		const cache = this._getCacheObject(startDate, unit, step);

		const startDateTimestamp = startDate.valueOf();
		const endDateTimestamp = endDate.valueOf();
		const key = this._durationCacheKey(startDateTimestamp, endDateTimestamp);
		let duration;
		if(cache.durations[key] === undefined){
			const result = compute();
			cache.durations[key] = result.valueOf();

			// can't populate end date due to logic of end date calculation, current unit tests capture it
			// cache.endDates[this._endDateCacheKey(startDateTimestamp, result)] = endDateTimestamp;
			duration = result;
		}else{
			duration = cache.durations[key];
		}

		return duration;
	}

	clear(): void{
		this._cache = {};
	}
}

