import vue from 'vue'
import vuex from 'vuex'
vue.use(vuex)

export default new vuex.Store({
    state:{
        menu:[],
    },
    mutations:{
        setMenu(state, menuList){
            state.menu = menuList


            
        }
    },
    getters:{
        getMenu(state){
            return state.menu
        }
    }
})
