/**
 * 路由配置常量
 * 集中管理所有路由路径，避免硬编码
 */

// 路由路径常量
export const ROUTE_PATHS = {
  // 基础路由
  LOGIN: '/',
  INDEX: '/Index',
  
  // 功能路由
  HOME: '/Home',
  PROCESS_MANAGE: '/ProcessManage',
  USER_MANAGE: '/UserManage',
  SCHEDULE_ALGORITHM: '/ScheduleAlgorithm',
  SCHEDULE_GANTT: '/ScheduleGantt',
  
  // 旧路由（保持兼容）
  TEST: '/Test',  // 重定向到 SCHEDULE_ALGORITHM
}

// 路由名称常量
export const ROUTE_NAMES = {
  LOGIN: 'login',
  INDEX: 'index',
  HOME: 'home',
  PROCESS_MANAGE: 'processManage',
  USER_MANAGE: 'userManage',
  SCHEDULE_ALGORITHM: 'scheduleAlgorithm',
  SCHEDULE_GANTT: 'scheduleGantt',
  TEST: 'test',
}

// 菜单配置（用于侧边栏）
export const MENU_CONFIG = [
  {
    id: 'home',
    path: ROUTE_PATHS.HOME,
    name: '系统首页',
    icon: 'el-icon-s-home',
    order: 1,
  },
  {
    id: 'processManage',
    path: ROUTE_PATHS.PROCESS_MANAGE,
    name: '数据采集及处理',
    icon: 'el-icon-s-order',
    order: 2,
  },
  {
    id: 'userManage',
    path: ROUTE_PATHS.USER_MANAGE,
    name: '人员管理',
    icon: 'el-icon-user-solid',
    order: 3,
  },
  {
    id: 'scheduleAlgorithm',
    path: ROUTE_PATHS.SCHEDULE_ALGORITHM,
    name: '流程分析资源调配',
    icon: 'el-icon-cpu',
    order: 4,
  },
  {
    id: 'scheduleGantt',
    path: ROUTE_PATHS.SCHEDULE_GANTT,
    name: '检验动态驾驶舱',
    icon: 'el-icon-date',
    order: 5,
  },
]

// 路由元信息
export const ROUTE_META = {
  HOME: { title: '系统首页' },
  PROCESS_MANAGE: { title: '数据采集及处理' },
  USER_MANAGE: { title: '人员管理' },
  SCHEDULE_ALGORITHM: { title: '流程分析资源调配' },
  SCHEDULE_GANTT: { title: '检验动态驾驶舱' },
}

// 组件路径映射
export const COMPONENT_PATHS = {
  LOGIN: '../components/Login.vue',
  INDEX: '../components/Index.vue',
  HOME: '../components/Home.vue',
  PROCESS_MANAGE: '../components/process/ProcessManage.vue',
  USER_MANAGE: '../components/user/UserManage.vue',
  SCHEDULE_ALGORITHM: '../components/user/ScheduleRun.vue',
  SCHEDULE_GANTT: '../components/user/ScheduleGantt.vue',
}

export default {
  ROUTE_PATHS,
  ROUTE_NAMES,
  MENU_CONFIG,
  ROUTE_META,
  COMPONENT_PATHS,
}
